vc=${1:-/dev/console}; shift

if [ ! -c "$vc" ]; then
	echo "$vc: Invalid terminal" >&2
	echo "Usage: \${0##*/} <vc> [<command> <arguments>]"
	exit 1
else
	exec setsid "${@:-/bin/sh}" < $vc > $vc 2> $vc
fi
